import React from "react";
import { useState, useEffect, useRef } from "react";

function Modal() {
  const [modal, setModal] = useState(false);
  const handleModal = () => {
    setModal(!modal);
  };
  const abc = document.querySelector(".moadl");
  const xyz = document.querySelector(".btn");
  useEffect(() => {
    xyz.addEventListener("click", handleModal);
  }, []);
  return (
    <>
      <dialog className="modal">
        <h1>hello</h1>
      </dialog>

      <button className="btn" onClick={handleModal}>
        close
      </button>
    </>
  );
}

export default Modal;
