import React from "react";
import { connect } from "react-redux";

import { useRef, useEffect, useState } from "react";
import "./Modal.css";
import Loader from "./Loader";
const url = "https://official-joke-api.appspot.com/random_joke";

function Modal({ setup, punchline }) {
  const modalContainer = useRef(null);

  const handleSubmit = () => {
    modalContainer.current.showModal();
  };
  const handleClose = () => {
    modalContainer.current.close();
  };
  const [joke, setJoke] = useState({ set: "", pun: "" });

  const [loading, setLoading] = useState(false);

  const handleJoke = async () => {
    setLoading(true);
    try {
      const response = await fetch(url);
      const data = await response.json();
      setLoading(false);
      setJoke((prev) => {
        return { ...prev, set: data.setup, pun: data.punchline };
      });
    } catch (error) {}
  };

  useEffect(() => {
    handleJoke();
  }, []);
  return (
    <>
      <dialog ref={modalContainer} className="modal">
        <h1>Random Jokes</h1>
        {loading ? <Loader /> : <h2>{joke.set}</h2>}
        <button className="open-modal" onClick={handleJoke}>
          Generate Joke
        </button>
        <button onClick={handleClose} className="close-modal">
          close
        </button>
      </dialog>

      <button className="open-modal" onClick={handleSubmit}>
        open modal
      </button>
    </>
  );
}

export default connect()(Modal);
