import React from "react";
import { connect } from "react-redux";

import { useRef, useEffect } from "react";
import "./Modal.css";
function Modal({ setup, punchline, handleJoke }) {
  const modalContainer = useRef(null);

  const handleSubmit = () => {
    modalContainer.current.showModal();
  };
  const handleClose = () => {
    modalContainer.current.close();
  };

  return (
    <>
      <dialog ref={modalContainer} className="modal">
        <h1>Random Jokes</h1>
        <h2>{setup}</h2>
        <h3>{punchline}</h3>
        <button className="open-modal" onClick={() => handleJoke()}>
          Generate Joke
        </button>
        <button onClick={handleClose} className="close-modal">
          close
        </button>
      </dialog>

      <button className="open-modal" onClick={handleSubmit}>
        open modal
      </button>
    </>
  );
}

const mapStateToProps = (state) => {
  const { joke } = state;
  const { setup, punchline } = joke;
  return { setup, punchline };
};
const mapDispatchToProps = (dispatch) => {
  return { handleJoke: () => dispatch({ type: "GENERATE_JOKE" }) };
};

export default connect(mapStateToProps, mapDispatchToProps)(Modal);
