import React from "react";
import { connect } from "react-redux";

import { useRef, useEffect, useState } from "react";
import "./Modal.css";
import Loader from "./Loader";

const url = "https://official-joke-api.appspot.com/random_joke";
const handleJokeData = async () => {
  const response = await fetch(url);
  const data = await response.json();
  const jokeSet = data.setup;
  const jokePun = data.punchline;

  return data;
};

function Modal({ setup, punchline, handleJoke }) {
  const modalContainer = useRef(null);

  const handleSubmit = ({ set, pun }) => {
    modalContainer.current.showModal();
  };
  const handleClose = () => {
    modalContainer.current.close();
  };

  return (
    <>
      <dialog ref={modalContainer} className="modal">
        <h1>Random Jokes</h1>
        <h2>{}</h2>
        <h3>{}</h3>
        <button
          className="open-modal"
          onClick={(e) => {
            e.preventDefault();

            return handleJoke();
          }}
        >
          Generate Joke
        </button>
        <button onClick={handleClose} className="close-modal">
          close
        </button>
      </dialog>

      <button className="open-modal" onClick={handleSubmit}>
        open modal
      </button>
    </>
  );
}
const mapStateToProps = (state) => {
  const {
    joke: { set, pun },
  } = state;

  return { set, pun };
};

const mapDispatchToPros = (dispatch) => {
  return {
    handleJoke: () => {
      const hh = async () => {
        const newData = await handleJokeData();
        return newData;
      };
      console.log(hh);
      return dispatch({
        type: "HANDLE_JOKE",
        payload: { hh },
      });
    },
  };
};
export default connect(mapStateToProps, mapDispatchToPros)(Modal);
