// import { initialState } from "./App";
import { v4 as uuidv4 } from "uuid";

const reducer = (state, action) => {
  if (action.type === "INCREASE_VALUE") {
    return { ...state, count: state.count + 1 };
  }
  if (action.type === "DECREASE_VALUE") {
    if (state.count === 0) {
      return { ...state, count: 0 };
    }

    return { ...state, count: state.count - 1 };
  }
  if (action.type === "CHANGE_NAME") {
    const nameValue = action.payload;
    return { ...state, name: nameValue };
  }

  if (action.type === "CHANGE_AGE") {
    const ageValue = action.payload;

    return { ...state, age: ageValue };
  }
  if (action.type === "CHANGE_EMAIL") {
    const emailValue = action.payload;

    return { ...state, email: emailValue };
  }
  if (action.type === "HANDLE_SUBMIT") {
    const emailValue = action.payload;
    const newState = { ...state, id: uuidv4() };

    console.log(state.totalData);
    return {
      ...state,
      totalData: [...newState],
    };
  }
  return state;
};

export default reducer;
